import React, { useState } from 'react';
import { useInView } from 'react-intersection-observer';
import Skeleton from './Skeleton';

const Timeline = () => {
  const [expandedIndex, setExpandedIndex] = useState(null);
  const [loadedIndexes, setLoadedIndexes] = useState([]);

  const events = [
    { date: '1892', title: 'Título 1', subtitle: 'Subtítulo 1', description: 'Descripción larga del Evento 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', image: 'https://via.placeholder.com/150' },
    { date: '1900', title: 'Título 2', subtitle: 'Subtítulo 2', description: 'Descripción larga del Evento 2. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', image: 'https://via.placeholder.com/150' },
    { date: '1921', title: 'Título 3', subtitle: 'Subtítulo 3', description: 'Descripción larga del Evento 3. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', image: 'https://via.placeholder.com/150' },
    { date: '1924', title: 'Título 4', subtitle: 'Subtítulo 4', description: 'Descripción larga del Evento 4. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', image: 'https://via.placeholder.com/150' },
    { date: '1998', title: 'Título 5', subtitle: 'Subtítulo 5', description: 'Descripción larga del Evento 5. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', image: 'https://via.placeholder.com/150' },
  ];

  const toggleDescription = (index) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  const handleInView = (index) => {
    if (!loadedIndexes.includes(index)) {
      setLoadedIndexes((prev) => [...prev, index]);
    }
  };

  return (
    <div className="timeline relative flex flex-col items-center px-4">
      <div className="absolute w-1 bg-gray-300 dark:bg-gray-600 h-full left-1/2 transform -translate-x-1/2"></div>
      {events.map((event, index) => {
        const { ref, inView } = useInView({
          triggerOnce: true,
          onChange: (inView) => inView && handleInView(index),
        });

        return (
          <div key={index} ref={ref} className={`timeline-item flex flex-col items-center mb-16 w-full max-w-md ${index % 2 === 0 ? 'items-start' : 'items-end'}`}>
            <button className={`timeline-date bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 py-1 px-3 rounded-full mb-4 ${index % 2 === 0 ? 'mr-8 -ml-8' : 'ml-8 -mr-8'}`}>{event.date}</button>
            {loadedIndexes.includes(index) ? (
              <div className={`timeline-content bg-light-card dark:bg-dark-card text-light-text dark:text-dark-text p-4 rounded-lg shadow-md relative hover:shadow-lg transition-shadow duration-300 max-w-xs`}>
                <h3 className="text-xl font-bold mb-2">{event.title}</h3>
                <h4 className="text-lg mb-2">{event.subtitle}</h4>
                <p className="text-base">{event.description}</p>
                <img src={event.image} alt={event.title} className="mt-4 rounded" />
              </div>
            ) : (
              <Skeleton />
            )}
          </div>
        );
      })}
    </div>
  );
};

export default Timeline;
